import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

const Chat = ({ user }) => {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    socket.on("chat message", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.off("chat message");
    };
  }, []);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === "") return;

    // Lähetetään objekti: { username, text }
    socket.emit("chat message", { username: user.username, text: message });
    setMessage("");
  };

  return (
    <div>
      <h2>Chat</h2>
      <div style={{ border: "1px solid gray", padding: "10px", height: "200px", overflowY: "scroll" }}>
        {messages.map((msg, idx) => (
          <div key={idx}>
            <strong>{msg.username}: </strong> {msg.text}
          </div>
        ))}
      </div>
      <form onSubmit={sendMessage}>
        <input value={message} onChange={(e) => setMessage(e.target.value)} placeholder="Kirjoita viesti" />
        <button type="submit">Lähetä</button>
      </form>
    </div>
  );
};

export default Chat;
