import React, { useState, useEffect } from "react";
import api from "../api";

const Profile = ({ user }) => {
  const [profile, setProfile] = useState({
    username: "",
    email: "",
    bio: "",
  });

  const [message, setMessage] = useState("");

  // Hae käyttäjän profiili kun komponentti latautuu
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await api.get(`/auth/profile/${user.id}`);
        setProfile(res.data);
      } catch (err) {
        console.error(err);
        setMessage("Virhe profiilin haussa");
      }
    };

    if (user?.id) {
      fetchProfile();
    }
  }, [user]);

  // Päivitä profiili
  const handleUpdate = async () => {
    try {
      const res = await api.put(`/auth/profile/${user.id}`, {
        username: profile.username,
        bio: profile.bio,
      });

      setProfile(res.data);
      setMessage("Profiili päivitetty!");
    } catch (err) {
      console.error("Update error:", err.response?.data || err);
      setMessage("Virhe profiilin päivityksessä");
    }
  };

  return (
    <div>
      <h2>Profiili</h2>

      <div>
        <label>Käyttäjänimi:</label>
        <input
          type="text"
          value={profile.username}
          onChange={(e) =>
            setProfile({ ...profile, username: e.target.value })
          }
        />
      </div>

      <div>
        <label>Bio:</label>
        <input
          type="text"
          value={profile.bio || ""}
          onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
        />
      </div>

      <div>
        <label>Email:</label>
        <input type="text" value={profile.email} disabled />
      </div>

      <button onClick={handleUpdate}>Tallenna muutokset</button>

      {message && <p>{message}</p>}
    </div>
  );
};

export default Profile;
