import React, { useState, useEffect } from "react";
import api from "../api";

const Chat = ({ user }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  // Hae viestit backendist채
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const res = await api.get("/chat");
        setMessages(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchMessages();
  }, []);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!newMessage) return;

    try {
      const res = await api.post("/chat", {
        userId: user.id,
        content: newMessage
      });
      setMessages([...messages, { ...res.data, username: user.username }]);
      setNewMessage("");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "0 auto" }}>
      <h2>Chat</h2>
      <div style={{ border: "1px solid #ccc", padding: "1rem", height: "300px", overflowY: "scroll" }}>
        {messages.map((msg) => (
          <div key={msg.id}>
            <strong>{msg.username}: </strong>{msg.content}
          </div>
        ))}
      </div>
      <form onSubmit={handleSend} style={{ marginTop: "1rem" }}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Kirjoita viesti..."
          style={{ width: "70%" }}
        />
        <button type="submit" style={{ width: "28%", marginLeft: "2%" }}>L채het채</button>
      </form>
    </div>
  );
};

export default Chat;
