import React, { useState, useEffect } from "react";
import api from "../api";

const Profiles = ({ user, token }) => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [bio, setBio] = useState("");
  const [message, setMessage] = useState("");

  // Hae profiilitiedot backendistä
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await api.get(`/api/auth/profile/${user.id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsername(res.data.username);
        setEmail(res.data.email);
        setBio(res.data.bio || "");
      } catch (err) {
        console.error(err);
        setMessage("Palvelinvirhe profiilia haettaessa");
      }
    };
    if (user?.id) fetchProfile();
  }, [user, token]);

  // Päivitä profiili
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await api.put(
        `/api/auth/profile/${user.id}`,
        { username, bio },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage("Profiili päivitetty!");
      console.log(res.data);
    } catch (err) {
      console.error(err);
      setMessage("Palvelinvirhe profiilia päivitettäessä");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "0 auto", padding: "1rem" }}>
      <h2>Profiilisi</h2>
      <form onSubmit={handleUpdate}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label>Email (vain luku):</label>
          <input type="email" value={email} disabled />
        </div>
        <div>
          <label>Bio:</label>
          <textarea
            value={bio}
            onChange={(e) => setBio(e.target.value)}
            placeholder="Kirjoita jotain itsestäsi..."
          />
        </div>
        <button type="submit">Tallenna</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Profiles;
