import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";
import "./Admin.css";

const Admin = ({ user }) => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await api.get("/admin/users");
      setUsers(res.data);
    } catch (err) {
      console.error("Failed to fetch users:", err);
      setError("Failed to load users");
    }
  };

  const handleToggleAdmin = async (userId) => {
    try {
      await api.put(`/admin/users/${userId}/toggle-admin`);
      fetchUsers(); // Refresh the list
    } catch (err) {
      console.error("Failed to toggle admin:", err);
      alert("Failed to update admin status");
    }
  };

  const handleDeleteUser = async (userId) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;

    try {
      await api.delete(`/admin/users/${userId}`);
      fetchUsers(); // Refresh the list
    } catch (err) {
      console.error("Failed to delete user:", err);
      alert("Failed to delete user");
    }
  };

  return (
    <div className="admin-container">
      <div className="admin-header">
        <h2>Admin Panel</h2>
        <button className="btn btn-back" onClick={() => navigate("/chat")}>
          Back to Chat
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="users-table-container">
        <table className="users-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Username</th>
              <th>Email</th>
              <th>Admin</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((u) => (
              <tr key={u.id} className={u.is_admin ? "admin-row" : ""}>
                <td>{u.id}</td>
                <td>{u.username}</td>
                <td>{u.email}</td>
                <td>
                  <span className={`badge ${u.is_admin ? "badge-admin" : "badge-user"}`}>
                    {u.is_admin ? "Admin" : "User"}
                  </span>
                </td>
                <td>
                  <div className="action-buttons">
                    {u.id !== user.id && (
                      <>
                        <button
                          className="btn-action btn-toggle"
                          onClick={() => handleToggleAdmin(u.id)}
                        >
                          {u.is_admin ? "Remove Admin" : "Make Admin"}
                        </button>
                        <button
                          className="btn-action btn-delete"
                          onClick={() => handleDeleteUser(u.id)}
                        >
                          Delete
                        </button>
                      </>
                    )}
                    {u.id === user.id && <span className="text-muted">You</span>}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Admin;
